import React, { useState } from 'react';

const App = () => {
  const [show, setShow] = useState(true);

  if (show) {
    return [
      {
        action: "EXCLUIR",
        onConfirm: this.onDelete,
        mensagem: "Deseja realmente excluir este registro?"
      }
    ]
  }
  return <button onClick={() => setShow(true)}>Excluir</button>
}

export default App;
