import React, { useState } from 'react';

const App = () => {
  const [show, setShow] = useState(true);

  if (show) {
    return [
      {
        action: "EXCLUIR",
        onConfirm: this.onDelete,
        mensagem: "Deseja realmente excluir este registro?"
      }
    ]
  }
  actionsDelete = (rowData, column) => this.openConfirm(rowData, "EXCLUIR");

  return <button onDelete={this.actionsDelete} onClick={() => setShow(true)}>Excluir</button>
}

export default App;
